// import logo from "./logo.svg";
// import "./App.css";
import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
// import { load } from "npm";

function Breakfast() {
  const [meals, setMeals] = useState([]);
  const loadMeals = async () => {
    // fetch("https://www.boredapi.com/api/activity")

    axios.get("http://localhost:8000/breakfast/").then((response) => {
      console.log(response.data);
      setMeals(response.data);
      // console.log(response.data);
    });
  };

  const removeMeal = (id) => {
    console.log(id);
    axios.delete("http://localhost:8000/breakfast/" + id);
    loadMeals();
    console.log(meals);
  };

  useEffect(() => {
    loadMeals();
    // console.log(recipeId);
  }, []);
  return (
    <div>
      <h1>Breakfast Meals</h1>
      <h3>
        {meals.map((meal) => (
          <div>
            <div key={meal.id}>
              {meal.name}{" "}
              {meal.instructions.map((step) => (
                <div key={step.number}> {step.step}</div>
              ))}
              <button
                onClick={() => {
                  removeMeal(meal.id);
                }}
              >
                delete
              </button>
            </div>
          </div>
        ))}
      </h3>
      <button>
        <Link to="/SearchRecipes"> Add Recipe</Link>
      </button>
    </div>
  );
}

export default Breakfast;
