// import logo from "./logo.svg";
// import "./App.css";
import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import { recipeIdContext } from "../Context";
import { Link } from "react-router-dom";

function App() {
  const [recipe, setRecipe] = useState([]);
  const [mealInput, setMealInput] = useState("");
  const [meal, setMeal] = useState("");
  const [recipeId, setRecipeId] = useState("");

  let formRef = useRef();
  let api_key = "?apiKey=83e419f9ed354ccc86b57c36f95c7811";
  const loadRecipe = async () => {
    // fetch("https://www.boredapi.com/api/activity")

    axios
      .get(
        "https://api.spoonacular.com/recipes/complexSearch" +
          api_key +
          "&query=" +
          mealInput
      )
      .then((response) => {
        setRecipe(response.data.results);
        // console.log(response);
        // console.log(response.data);
        console.log(recipe);
      });
  };

  // useEffect(() => {
  //   loadRecipe();
  // }, []);

  const handleChange = (e) => {
    setMealInput(e.target.value);
    console.log(e.target.value);
  };

  const submit = (e) => {
    e.preventDefault();
    setMeal(mealInput);
    console.log(meal);
    formRef.current?.reset();
    loadRecipe();
    setMealInput("");
  };

  const select = (id) => {
    console.log(id);
    setRecipeId(id);

    // axios
    //   .get(
    //     "https://api.spoonacular.com/recipes/" +
    //       id +
    //       "/analyzedInstructions" +
    //       api_key
    //   )
    //   .then((response) => {
    //     // console.log(response);
    //     // console.log(response.data);
    //     console.log(response.data[0].steps);
    //     let steps = response.data[0].steps;
    //     setInstructions(steps);
    //   });
  };

  return (
    <recipeIdContext.Provider value={{ recipeId, setRecipeId }}>
      <div>
        <h3>
          {recipe.map((recipe) => (
            <div>
              <button key={recipe.id} onClick={() => select(recipe.id)}>
                <Link to="/RecipeInstructions"> </Link>
                {recipe.title}
              </button>
            </div>
          ))}
        </h3>
        {/* <h3>{meal}</h3> */}
        <input value={mealInput} onChange={handleChange} />
        <button onClick={submit}>Enter</button>
      </div>
    </recipeIdContext.Provider>
  );
}

export default App;
