// import logo from "./logo.svg";
// import "./App.css";
import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
// import { load } from "npm";
// import { load } from "npm";

function Breakfast() {
  const [meals, setMeals] = useState([]);
  const [ingredients, setIngredients] = useState([]);
  const loadMeals = async () => {
    // fetch("https://www.boredapi.com/api/activity")

    axios.get("http://localhost:8000/breakfast/").then((response) => {
      console.log(response.data);
      setMeals(response.data);
      // console.log(response.data);
    });
  };

  const removeMeal = async (id) => {
    console.log(id);
    try {
      await axios
        .delete(`http://localhost:8000/breakfast/${id}`)
        .then((response) => {
          console.log(response);
          loadMeals();
          console.log(meals);
        });
    } catch {
      console.log("error");
      loadMeals();
    }
  };

  useEffect(() => {
    loadMeals();
    // console.log(recipeId);
  }, []);
  return (
    <div>
      <h1>Breakfast Meals</h1>
      <h3>Ingredients</h3>

      <div>
        {meals.map((meal) => (
          <div>
            <h4 key={meal.id}>
              {meal.name}
              <h3>Instructions</h3>
              {meal.instructions.map((step) => (
                <h5 key={step.number}> {step.step}</h5>
              ))}
              <button
                onClick={() => {
                  removeMeal(meal.id);
                }}
              >
                delete
              </button>
            </h4>
          </div>
        ))}
      </div>
      <button>
        <Link to="/SearchRecipes"> Add Recipe</Link>
      </button>
    </div>
  );
}

export default Breakfast;
