import React, { useEffect, useState, useRef } from "react";
import axios from "axios";

function App() {
  const [recipe, setRecipe] = useState([]);
  const [mealInput, setMealInput] = useState("");
  const [meal, setMeal] = useState("");
  let formRef = useRef();
  let api_key = "?apiKey=83e419f9ed354ccc86b57c36f95c7811";
  const loadRecipe = async () => {
    // fetch("https://www.boredapi.com/api/activity")

    const { instructions } = await axios.post(
      "http://127.0.0.1:8000/recipes/" + mealInput,
      mealInput
    );
    const { data } = await axios.get(
      "http://127.0.0.1:8000/recipes/" + mealInput
    );
    // axios
    //   // .post("http://127.0.0.1:8000/recipes/" + mealInput)
    //   // .then((response) => response.json())
    //   .get("http://127.0.0.1:8000/recipes/" + mealInput)
    //   .then((data) => {
    //     setRecipe(data.results);
    //     console.log(data.results);
    //     // console.log({ recipe });
    //   });
    console.log(data[0]);
    setRecipe(data);
  };

  // useEffect(() => {
  //   loadRecipe();
  // }, []);

  const handleChange = (e) => {
    setMealInput(e.target.value);
    console.log(e.target.value);
  };

  const submit = (e) => {
    e.preventDefault();
    setMeal(mealInput);
    console.log(meal);
    formRef.current?.reset();
    loadRecipe();
    setMealInput("");
  };

  const select = (id) => {
    console.log(id);
  };

  return (
    <div>
      <h3>
        {recipe.map((recipe) => (
          <div>
            <button key={recipe.id} onClick={() => select(recipe.id)}>
              {recipe.name}
            </button>
          </div>
        ))}
      </h3>
      {/* <h3>{meal}</h3> */}
      <input value={mealInput} onChange={handleChange} />
      <button onClick={submit}>Enter</button>
    </div>
  );
}

export default App;
