// import logo from "./logo.svg";
// import "./App.css";
import React, { useEffect, useState, useRef, useContext } from "react";
import axios from "axios";
import { recipeIdContext, recipeNameContext } from "../Context";
import { Link } from "react-router-dom";

function App() {
  const [recipe, setRecipe] = useState([]);
  const [mealInput, setMealInput] = useState("");
  const [meal, setMeal] = useState("");
  // const [recipeId, setRecipeId] = useState("");
  const { recipeId, setRecipeId } = useContext(recipeIdContext);
  const { recipeName, setRecipeName } = useContext(recipeNameContext);

  let formRef = useRef();
  // let api_key = process.env.api_key;
  let api_key = "?apiKey=782bba4ef5fd462d81b2102ebb96fe55";
  const loadRecipe = async () => {
    // fetch("https://www.boredapi.com/api/activity")

    axios
      .get(
        "https://api.spoonacular.com/recipes/complexSearch" +
          api_key +
          "&query=" +
          mealInput
      )
      .then((response) => {
        setRecipe(response.data.results);
        // console.log(response);
        // console.log(response.data);
        console.log(recipe);
      });
  };

  // useEffect(() => {
  //   loadRecipe();
  // }, []);

  const handleChange = (e) => {
    setMealInput(e.target.value);
    console.log(e.target.value);
  };

  const submit = (e) => {
    e.preventDefault();
    setMeal(mealInput);
    console.log(meal);
    formRef.current?.reset();
    loadRecipe();
    setMealInput("");
  };

  const select = (id, name) => {
    setRecipeId(id);
    console.log(recipeId);
    setRecipeName(name);
    console.log(recipeName);

    // axios
    //   .get(
    //     "https://api.spoonacular.com/recipes/" +
    //       id +
    //       "/analyzedInstructions" +
    //       api_key
    //   )
    //   .then((response) => {
    //     // console.log(response);
    //     // console.log(response.data);
    //     console.log(response.data[0].steps);
    //     let steps = response.data[0].steps;
    //     setInstructions(steps);
    //   });
  };

  return (
    <div>
      <h3>
        {recipe.map((recipe) => (
          <div>
            <button
              key={recipe.id}
              onClick={() => select(recipe.id, recipe.title)}
            >
              <Link to="/RecipeInstructions"> {recipe.title}</Link>
            </button>
            <h4>{recipeId}</h4>
          </div>
        ))}
      </h3>
      {/* <h3>{meal}</h3> */}
      <input value={mealInput} onChange={handleChange} />
      <button onClick={submit}>Enter</button>
      <button>
        <Link to="/"> Home</Link>
      </button>
    </div>
  );
}

export default App;
