import React, { useEffect, useState, useRef, useContext } from "react";
import { recipeIdContext } from "../Context";
import axios from "axios";

function RecipeInstructions() {
  let api_key = "?apiKey=83e419f9ed354ccc86b57c36f95c7811";
  const [instructions, setInstructions] = useState("");

  const { recipeId, setRecipeId } = useContext(recipeIdContext);

  const select = (recipeId) => {
    console.log("ehll");
    console.log(recipeId);
    axios
      .get(
        "https://api.spoonacular.com/recipes/" +
          recipeId +
          "/analyzedInstructions" +
          api_key
      )
      .then((response) => {
        // console.log(response);
        // console.log(response.data);
        console.log(response.data[0].steps);
        let steps = response.data[0].steps;
        setInstructions(steps);
        console.log(instructions);
      });
  };
  useEffect(() => {
    select();
  }, []);

  return (
    <h3>
      {/* {instructions.map((instruction) => (
        <div key={instruction.number}> {instruction.step}</div>
      ))} */}
    </h3>
  );
}

export default RecipeInstructions;
