import logo from "./logo.svg";
import "./App.css";
import React, { useEffect, useState } from "react";

function App() {
  const [recipe, setRecipe] = useState({});
  const loadRecipe = () => {
    fetch("http://localhost:8000/recipes/")
      .then((response) => response.json())
      .then((data) => {
        setRecipe(data);
        console.log(data);
      });
  };

  useEffect(() => {
    loadRecipe();
  });

  return (
    <div>
      <h1>{recipe.name}</h1>
      {/* <button onClick={loadRecipe}>Update</button> */}
    </div>
  );
}

export default App;
